import { useState } from "react";
import { FiCheckSquare, FiTrash } from "react-icons/fi"


import { Container, Header, Content, Task } from "./styles";

interface TaskItem {
  id: number;
  title: string;
  isComplete: boolean;
}

 export function TaskList(){
  const[tasks, setTasks] = useState<TaskItem[]>([]);
  const[newTaskTitle, setNewTaskTitle] = useState('');

  function handleCreateNewItem(){
    if(!newTaskTitle){
      return;
    }
    const task = {
      id: Math.random(),
      title: newTaskTitle,
      isComplete: false,
    }
    setNewTaskTitle('');
    setTasks([...task, task])
  }

   return(
     <Container>
       <Header>
         <h2>Minha List</h2>

         <div>
            <input type="text" />
            <button type="submit">
              <FiCheckSquare size={16} color="#FFF" />
            </button>
         </div>
       </Header>
       <Content>
         <ul>
           <li>
             <Task>
                <input type="checkbox" />
                <p>Estudar</p>
             </Task>
             <button type="button">
               <FiTrash size="16" />
             </button>
           </li>
         </ul>
       </Content>
     </Container>
   );
 }
